statements.statement:

INSERT INTO SAM_EMPLOYEE(
       NUM
     , NAME
     , JOBLEVEL_CODE
     , DIVISION_CODE
     , DEPARTMENT_CODE
     , BIRTHDATE
     , SEX
     , SSN
     , TELEPHONE
     , POSTAL
     , SKILL_CODE
     , ADDRESS
) VALUES (
     ${num}
   , ${name}
   , ${joblevelCode}
   , ${divisionCode}
   , ${departmentCode}
   , ${birthdate}
   , ${sex}
   , ${ssn}
   , ${telephone}
   , ${postal}
   , ${skillCode}
   , ${address}
)


UPDATE SAM_EMPLOYEE
   SET NUM = NUM
     , NAME = ${name}
     , JOBLEVEL_CODE = ${joblevelCode}
     , DIVISION_CODE = ${divisionCode}
     , DEPARTMENT_CODE = ${departmentCode}
     , BIRTHDATE = ${birthdate}
     , SEX = ${sex}
     , SSN = ${ssn}
     , TELEPHONE = ${telephone}
     , POSTAL = ${postal}
     , SKILL_CODE = ${skillCode}
     , ADDRESS = ${address}
 WHERE NUM = ${num}
