statements.statement:

INSERT INTO SAM_USER (
      USR_ID
    , USR_NM
    , USR_PW
    , REG_DT
    , CHNG_USR_ID
    , CHNG_DT
    , LANG_CLCD
    , ROLE
) VALUES (
      ${usrId}
    , ${usrNm}
    , ${usrPw}
    , TO_CHAR(CURRENT_TIMESTAMP,'YYYYMMDDHH24MISS')
    , ${chngUsrId}
    , TO_CHAR(CURRENT_TIMESTAMP,'YYYYMMDDHH24MISS')
    , ${language}
    , ${role}
)


UPDATE SAM_USER
   SET USR_ID = ${usrId}
       , USR_NM = ${usrNm}
       , USR_PW = ${usrPw}
       , ROLE = ${role}
       , CHNG_USR_ID = ${chngUsrId}
       , CHNG_DT = TO_CHAR(CURRENT_TIMESTAMP,'YYYYMMDDHH24MISS')
       , LANG_CLCD = ${language}
 WHERE USR_ID = ${usrId}
