package testJava.vol1.ch09;

public class AnonymousExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Anonymous anony = new Anonymous();
		// 익명 객체 필드 사용
		anony.field.wake();
		// 익명 객체 로컬 변수 사용
		anony.method1();
		
		// 익명 객체 매개값 사용
		anony.method2(
			new Person() {
				void study() {
					System.out.println("공부 합니다.");
				}
				@Override
				void wake() {
					System.out.println("8에 일어납니다.");
					study();
				}
			}
		);
	}

}
